const initialState = {
  items: {},
  totalPrice: 0,
  totalCount: 1,
};

const cart = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_TOTAL_PRICE':
      return {
        ...state,
        totalProce: action.payload,
      };
    case 'SET_TOTAL_COUNT':
      return {
        ...state,
        totalCount: action.payload,
      };
    default:
      return state;
  }
};

export default cart;
